{"metadata":{"kernelspec":{"name":"ir","display_name":"R","language":"R"},"language_info":{"name":"R","codemirror_mode":"r","pygments_lexer":"r","mimetype":"text/x-r-source","file_extension":".r","version":"4.0.5"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# This Python 3 environment comes with many helpful analytics libraries installed\n# It is defined by the kaggle/python Docker image: https://github.com/kaggle/docker-python\n# For example, here's several helpful packages to load\n\nimport numpy as np # linear algebra\nimport pandas as pd # data processing, CSV file I/O (e.g. pd.read_csv)\n\n# Input data files are available in the read-only \"../input/\" directory\n# For example, running this (by clicking run or pressing Shift+Enter) will list all files under the input directory\n\nimport os\nfor dirname, _, filenames in os.walk('/kaggle/input'):\n    for filename in filenames:\n        print(os.path.join(dirname, filename))\n\n# You can write up to 20GB to the current directory (/kaggle/working/) that gets preserved as output when you create a version using \"Save & Run All\" \n# You can also write temporary files to /kaggle/temp/, but they won't be saved outside of the current session","metadata":{"_uuid":"699362b2-d0ad-42e0-b8ca-8f44ec4ea026","_cell_guid":"2e15e289-da44-4bb6-9e10-c9dd3431c6e1","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# installed tidyverse, ggplot2 and lubridate\n\ninstall.packages('tidyverse')\nlibrary('tidyverse')\ninstall.packages('ggplot2')\ninstall.packages('lubridate')\nlibrary('ggplot2')\nlibrary('lubridate')\n\n# uploaded 2014 data into R studio \n# change names\n\nq1.2_data <- rename(Divvy_Trips_2014_Q1Q2)\nq3_data <- rename(Divvy_Trips_2014_Q3_07)\nq4_data <- rename(Divvy_Trips_2014_Q4)\n\n\n# using colnames I compared column names in the data set\n\ncolnames(q1.2_data)\ncolnames(q3_data)\ncolnames(q4_data)\n\n# Renaming columns to improve naming and workflow \n\nq1.2_data <- rename(q1.2_data,\n                    ride_id = trip_id,\n                    rideable_type = bikeid,\n                    started_at = starttime,\n                    ended_at = stoptime,\n                    start_station_name = from_station_name,\n                    start_station_id = from_station_id,\n                    end_station_name = to_station_name,\n                    end_station_id = to_station_id,\n                    member_casual = usertype)\n\nView(q1.2_data)\n\nq3_data <- rename(q3_data,\n                  ride_id = trip_id,\n                  rideable_type = bikeid,\n                  started_at = starttime,\n                  ended_at = stoptime,\n                  start_station_name = from_station_name,\n                  start_station_id = from_station_id,\n                  end_station_name = to_station_name,\n                  end_station_id = to_station_id,\n                  member_casual = usertype)\n\nView(q3_data)\n\nq4_data <- rename(q4_data,\n                  ride_id = trip_id,\n                  rideable_type = bikeid,\n                  started_at = starttime,\n                  ended_at = stoptime,\n                  start_station_name = from_station_name,\n                  start_station_id = from_station_id,\n                  end_station_name = to_station_name,\n                  end_station_id = to_station_id,\n                  member_casual = usertype)\n\nView(q4_data)\n\n#inspect data frames and look for inconguencies \n\nstr(q1.2_data)\nstr(q3_data)\nstr(q4_data)\n\n#convert ride_id and rideable_type to character so that they can stack\n\nq1.2_data <- mutate(q1.2_data, ride_id = as.character(ride_id),\n                    rideable_type = as.character(rideable_type))\n\nq3_data <- mutate(q3_data, ride_id = as.character(ride_id),\n                  rideable_type = as.character(rideable_type))\n\nq4_data <- mutate(q4_data, ride_id = as.character(ride_id),\n                  rideable_type = as.character(rideable_type))\n\nstr(q1.2_data)\nstr(q3_data)\nstr(q4_data)\n\n#stack individual quarters data frames into one big data frame\n\noriginal_data <- bind_rows(q1.2_data,q3_data,q4_data) # keep original all_trip data\n\nall_trips <- bind_rows(q1.2_data,q3_data,q4_data)\n\nView(all_trips)\n\n# remove data that will not be used \n\noriginal_data <- original_data %>% \n  select(-c(birthyear, gender))\n\nall_trips <- all_trips %>% \n    select(-c(birthyear, gender))\n\nView(all_trips)\n\n#inspect the new table \n\ncolnames(all_trips)\nnrow(all_trips)\ndim(all_trips)\nhead(all_trips)\nstr(all_trips)\nsummary(all_trips)\n\n#cleaning data - correct the naming of \n# subscribers = members and customers = casual to match current practices\n\ntable(all_trips$member_casual)\n\noriginal_data <- original_data %>% \n  mutate(member_casual = recode(member_casual,\n                                \"Subscriber\" = \"member\",\n                                \"Customer\" = \"casual\"))\n\nall_trips <- all_trips %>% \n  mutate(member_casual = recode(member_casual,\n                                \"Subscriber\" = \"member\",\n                                \"Customer\" = \"casual\"))\n\ntable(all_trips$member_casual)\n\nView(all_trips)\n\n\n# add columns that list date, month , day and year of each ride \n\nhead(all_trips$started_at)\n\n# all_trips$started_at <- as.Date(all_trips$started_at, format = \"%m-%d-%y\")\n# all_trips$date <- as.Date(all_trips$started_at)\n# all_trips$month <- as.Date(all_trips$date, format = \"%m\")\n# code affected all_trips incorrectly \n\nall_trips <- original_data\n\n#Symbol\tMeaning\tExample\n# %d\tday as a number (0-31)\t01-31\n# %a\tabbreviated weekday\tMon\n# %A\tunabbreviated weekday\tMonday\n# %m\tmonth (00-12)\t00-12\n# %b\tabbreviated month\tJan\n# %B\tunabbreviated month\tJanuary\n# %y\t2-digit year\t07\n# %Y\t4-digit year\t2007\n# as.Date = data containing m,d,y \n# as.POSIXct = data containing m,d,y, H:M and timezone \n\n# all_trips$date <- as.Date(all_trips$started_at) - error \nall_trips$date <- as.Date(all_trips$started_at, format = \"%m/%d/%Y\")\n\n# all_trips$month <- as.Date(all_trips$date, format = \"%m\"- did not pull data correctly \nall_trips$month <- format(as.Date(all_trips$date), \"%m\")\nall_trips$day <- format(as.Date(all_trips$date), \"%d\")\nall_trips$year <- format(as.Date(all_trips$date), \"%Y\")\nall_trips$day_of_week <- format(as.Date(all_trips$date), \"%A\")\n# use difftime (time difference) to get ride length column \n# all_trips$ride_length <- difftime(all_trips$ended_at,all_trips$started_at) - error \n# end and start columns are not formatted correctly \n#all_trips$started_at <- as.POSIXct(all_trips$started_at, format = \"%m%d%Y %H:%M\")\n# affected data incorrectly - turned data into NA\n# need to copy start_at column from original data \nall_trips$started_at <- original_data$started_at\nView(original_data)\nView(all_trips)\n\n#all_trips$started_at_2 <- as.POSIXct(all_trips$started_at, format = \"%m%d%Y %H:%M\")\n# still ended in NA - missing timezone format in code (tz = \"\")\n#all_trips$started_at_2 <- NULL\n#all_trips$ended_at_2 <- NULL \n\nhead(all_trips[, c(\"started_at\", \"ended_at\")])\n\n# Convert started_at and ended_at to POSIXct\nall_trips$started_at <- as.POSIXct(all_trips$started_at, format = \"%m/%d/%Y %H:%M\", tz = \"UTC\")\nall_trips$ended_at <- as.POSIXct(all_trips$ended_at, format = \"%m/%d/%Y %H:%M\", tz = \"UTC\")\n\n# Calculate ride_length\nall_trips$ride_length <- difftime(all_trips$ended_at, all_trips$started_at)\n\nstr(all_trips)\n# Convert \"ride_length\" from Factor to numeric so we can run calculations on the data\nis.factor(all_trips$ride_length) #FALSE\nall_trips$ride_length <- as.numeric(as.character(all_trips$ride_length))\nis.numeric(all_trips$ride_length) #TRUE\n\n# clean data - remove entries when bikes were taken out for quality checks or ride length is negative \n\nall_trips_v2 <- all_trips[!(all_trips$start_station_name == \"HQ QR\" | all_trips$ride_length<0),]\n\nhead(all_trips_v2)\ndim(all_trips)\ndim(all_trips_v2)\nany(all_trips_v2$ride_length < 0)\nany(all_trips_v2$start_station_name == \"HQ QR\")\n\n# descruptive analysis on ride_length (mean,median,max,min) \nmean(all_trips_v2$ride_length)\nmedian(all_trips_v2$ride_length)\nmax(all_trips_v2$ride_length)\nmin(all_trips_v2$ride_length)\n# or condense all four by using summary()\nsummary(all_trips_v2$ride_length)\n\n#compare members and casual users \naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual, FUN = mean)\naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual, FUN = median)\naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual, FUN = max)\naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual, FUN = min)\n\n# average ride time by each day by member type \naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual + all_trips_v2$day_of_week, \n          FUN = mean)\n# correct order of days of the week\nall_trips_v2$day_of_week <- ordered(all_trips_v2$day_of_week, levels=c(\"Sunday\",\"Monday\",\"Tuesday\",\n                                                                       \"wednesday\",\"Thursday\",\n                                                                       \"Friday\",\"Saturday\"))\n\naggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual + all_trips_v2$day_of_week, \n          FUN = mean)\n\n# analyze ridership data by type and weekday \n\nall_trips_v2 %>% \n  mutate(weekday = wday(started_at, label = TRUE)) %>% \n  group_by(member_casual, weekday) %>% \n  summarise(number_of_rides = n(),average_duration = mean(ride_length)) %>% \n  arrange(member_casual, weekday)\n\n# visualize the number of rides by rider type \n\nall_trips_v2 %>% \n  mutate(weekday = wday(started_at, label = TRUE)) %>% \n  group_by(member_casual, weekday) %>% \n  summarise(number_of_rides = n(), average_duration = mean(ride_length)) %>% \n  arrange(member_casual, weekday) %>% \n  ggplot(aes(x = weekday, y = number_of_rides, fill = member_casual)) +\n  geom_col(position = \"dodge\")\n\n# visualize the average duration of rides \n\nall_trips_v2 %>% \n  mutate(weekday = wday(started_at, label = TRUE)) %>% \n  group_by(member_casual, weekday) %>% \n  summarise(number_of_riders = n(), average_duration = mean(ride_length)) %>% \n  arrange(member_casual, weekday) %>% \n  ggplot(aes(x = weekday, y= average_duration, fill = member_casual)) +\n  geom_col(position = \"dodge\")\n\ncounts <- aggregate(all_trips_v2$ride_length ~ all_trips_v2$member_casual + all_trips_v2$day_of_week, FUN = mean)\n\nwrite.csv(counts, file = '~/Desktop/Divvy_Exercise/avg_ride_length.csv')\n\n\n\n\n\n\n\n\n  \n\n\n\n\n\n\n","metadata":{"_uuid":"249a64b4-ea5a-41bf-8769-43780bc183cf","_cell_guid":"66ac7b1b-f029-4b67-a84e-14a1c5ef00f5","collapsed":false,"jupyter":{"outputs_hidden":false},"execution":{"iopub.status.busy":"2023-12-12T16:17:18.813674Z","iopub.execute_input":"2023-12-12T16:17:18.816757Z"},"trusted":true},"execution_count":null,"outputs":[{"name":"stderr","text":"Installing package into ‘/usr/local/lib/R/site-library’\n(as ‘lib’ is unspecified)\n\n","output_type":"stream"}]}]}